def find_string(haystack, needles):
    if len(haystack) == 0 or len(needles) == 0:
        return []
    dictionary = set(needles)
    win_len, word_len = len(needles), len(needles[0])
    needle_freq = defaultdict(lambda: 0)
    for w in needles:
        needle_freq[w] += 1

    found_indices = []
    found_at = {}
    i = 0
    last_idx = len(haystack) - (win_len*word_len) + 1
    while i < last_idx:
        w = found_at.get(i, haystack[i:i+word_len])
        if w in dictionary:
            window_size = 0
            window = defaultdict(lambda: 0)
            j = i
            while j < len(haystack):
                w1 = found_at.get(j, haystack[j:j+word_len])
                if w1 in dictionary:
                    found_at[j] = w1
                    window[w1] += 1
                    window_size += 1
                    if window_size == win_len:
                        idx_start = j - (win_len - 1) * word_len
                        if window == needle_freq:
                            found_indices.append(idx_start)
                        word_remove = haystack[idx_start: idx_start + word_len]
                        window[word_remove] -= 1
                        window_size -= 1
                        if window[word_remove] == 0:
                            del window[word_remove]
                    j += word_len
                else:
                    break
        i += 1
    return list(sorted(list(set(found_indices))))


